window.otpResend=function(i,n){return{countDown:(i+0)*1e3+0,countDownTimer:Date.now()+((i+0)*1e3+0),intervalID:null,locale:n,init(){this.intervalID||(this.intervalID=setInterval(()=>{this.countDown=this.countDownTimer-new Date().getTime()},1e3))},getTime(){return this.countDown<0&&this.clearTimer(),this.countDown},formatTime(t){return this.pdigit(String(Math.floor(t%(1e3*60*60)/(1e3*60))).padStart(2,"0")+":"+String(Math.floor(t%(1e3*60)/1e3)).padStart(2,"0"))},clearTimer(){clearInterval(this.intervalID)},pdigit:function(t){return this.locale!=="fa"?t:t.toString().replace(/\d/g,function(r){return["۰","۱","۲","۳","۴","۵","۶","۷","۸","۹"][r]})}}};
